        for (uint256 i; i < _keys.length;) {
            data[i] = prices[_keys[i]];

            if (data[i].timestamp == 0) {
                data[i] = _fallbackCallRaw(_keys[i]);
            }

            unchecked { i++; }
        }
    }

    /// @dev this is only for dev debug,
